/** @jsxImportSource @emotion/react */
import {  css } from "@emotion/core";
import PricingTitle from "./PricingTitle";
import PricingInfo from "./PricingInfo";

const PricingCard = ({ level }) => (
  <div css={styles} className="pricingCard">
    <PricingTitle level={level} />
    <PricingInfo />
  </div>
);

const styles = css`
  width: 100%;
  max-width: 32%;
  padding: 40px 0;
  background: #1a1a1a;
  border: 1px solid transparent;
  text-align: center;
  cursor: pointer;
  transition: border 600ms ease-in-out, transform 300ms ease-in-out;
  &:hover {
    border: 1px solid #ff1414;
    transform: scale(1.2); // Scale the card up twice when hovering
  }
  @media (max-width: 799px) {
    max-width: 520px;
    margin-bottom: 26px;
  }
  @media (min-width: 800px) and (max-width: 1189px) {
    max-width: 48%;
    margin-bottom: 26px;
  }
`;

export default PricingCard;
