import React from "react";
import './styles.css'; // Importation du fichier CSS

//balise logo fitlearning
import { css } from "@emotion/core";

function Logo() {
  const styles = css`
    .logo-container {
      display: flex;
      align-items: center;
      justify-content: center;
      height: 60px;
      background-color: #fff;
      border-radius: 50px;
      box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.1);
    }

    .logo {
      margin: 0;
      font-size: 28px;
      font-weight: 700;
      text-transform: uppercase;
      color: #444;
      cursor: pointer;
      position: relative;
    }

    .logo::after {
      content: "";
      position: absolute;
      bottom: -4px;
      left: 0;
      width: 100%;
      height: 2px;
      background-color: #ff1414;
      opacity: 0;
      transition: opacity 0.2s ease-in-out;
    }

    .logo:hover {
      color: #FFFFFF;
    }

    .logo:hover::after {
      opacity: 1;
    }
  `;

  return (
    <div className="logo-container">
      <h1 css={styles} className="logo">
        FitLearning
      </h1>
    </div>
  );
}

export default Logo;
