import { initializeApp } from "firebase/app";
import { getAuth } from "firebase/auth";
import {collection,query,getFirestore} from"firebase/firestore";
import {  addDoc } from "firebase/firestore";

const firebaseConfig = {
  apiKey: "AIzaSyD86Rwy1oDjkiAvYKSDo55SvXhNxARzQUE",
  authDomain: "aws-fitlearning.firebaseapp.com",
  databaseURL: "https://aws-fitlearning-default-rtdb.firebaseio.com",
  projectId: "aws-fitlearning",
  storageBucket: "aws-fitlearning.appspot.com",
  messagingSenderId: "372431024684",
  appId: "1:372431024684:web:cecdc1c6882ba3e0241789",
  measurementId: "G-P72SEPPVBP"
};

const app = initializeApp(firebaseConfig);

export const auth = getAuth(app);
//initialiser les services
const db=getFirestore(app);

const productsRef = db.collection('products');

export const addProduct = (product) => {
  const productRef = db.ref('products/' + product.id);
  productRef.set({
    name: product.name,
    price: product.price,
    image: product.image.default, // Utilisez .default pour obtenir le chemin d'accès de l'image
  });
};

export const getProduct = (id) => {
  const productRef = db.ref('products/' + id);
  return productRef.once('value')
    .then((snapshot) => {
      const product = snapshot.val();
      return product;
    });
};
