/** @jsx jsx */




//les differentes formations proposées
import { css } from '@emotion/react';
import Title from './Title';
import Container from './Container';
import Card from './Card';
import IconOne from './IconOne';
import IconTwo from './IconTwo';
import IconThree from './IconThree';
import IconFour from './IconFour';

const Features = () => (
  <section css={styles} className="features" id="about">
    <Title
      title="NOS FORMATIONS"
      desc="On vous propose un programme 100% en ligne.Formez-vous à votre rythme, sans contrainte d’horaires quand vous êtes disponible ."
    />
    <Container>
      <Card cardImg={IconOne} cardTitle="Programme Personnel" cardDescription="Créez un programme d'entraînement personnalisé selon vos objectifs et vos disponibilités." />
      <Card cardImg={IconTwo} cardTitle="Programme Alimentaire" cardDescription="Obtenez des conseils nutritionnels pour atteindre vos objectifs et améliorer votre santé." />
      <Card cardImg={IconThree} cardTitle="Suivi de Progrés" cardDescription="Suivez votre évolution et votre progression pour vous motiver et ajuster votre entraînement." />
      <Card cardImg={IconFour} cardTitle="Astuces et Conseils de Coach" cardDescription="Profitez des conseils d'un coach sportif professionnel pour améliorer votre technique et vos performances." />
    </Container>
  </section>
);

const styles = css`
  width: 100%;
  padding: 170px 0;
  background: #000;
  .container {
    display: flex;
    flex-wrap: wrap;
    justify-content: space-between;
    padding: 80px 0 0 0;
    max-width: 960px;
    margin: 0 auto;
  }
  @media (max-width: 767px) {
    .title {
      padding: 0 20px;
      text-align: center;
    }
    .container {
      justify-content: center;
    }
  }
`;

export default Features;
